img.props:
({
                state
              }) => {
                const {
                  country
                } = state
                const {
                  countries
                } = state.parent
                if (!country || !countries) return
                return {
                  src: country && countries[country].src,
                  width: 'B1',
                  aspectRatio: '36 / 26'
                }
              }

