on.mouseover:
(ev, el, s) => {
          if (s.highlight) return
          el.TeamJoint.Info.meta.setProps({
            theme: 'team'
          })
          s.update({
            highlight: true
          })
        }

on.mouseout:
(ev, el, s) => {
          if (!s.highlight) return
          el.TeamJoint.Info.meta.setProps({
            theme: 'meta'
          })
          s.update({
            highlight: false
          })
        }

